    public static void RunCalculation()
    { 
        var lines = Input.Split('\n');
        var leftList = lines.Select(x => int.Parse(x.Split("   ")[0])).ToArray();
        var rightList = lines.Select(x => int.Parse(x.Split("   ")[1])).ToArray();

        // Sort both lists
        Array.Sort(leftList); 
        Array.Sort(rightList);

        // Calculate the total distance
        var totalDistance = leftList.Select((t, i) => Math.Abs(t - rightList[i])).Sum();

        // Output the result
        Console.WriteLine($"Total distance: {totalDistance}");
    }
