    public static void CalculateSafeReports()
    {
        var stopwatch = Stopwatch.StartNew();
        var lines = Input.Split('\n');
        var safeCount = 0;

        foreach (var line in lines)
        {
            var levels = line.Split(' ').Select(int.Parse).ToArray();
            
            if (IsSafe(levels) || CanBeSafeWithOneRemoval(levels))
            {
                safeCount++;
            }
        }

        stopwatch.Stop();
        var elapsedMs = stopwatch.ElapsedMilliseconds;
        
        Console.WriteLine($"Number of safe reports: {safeCount} (calculated in: {elapsedMs} ms)");
    }

    private static bool IsSafe(int[] levels)
    {
        if (levels.Length < 2)
        {
            return false;
        }

        // Determine the trend (increasing or decreasing)
        bool? isIncreasing = null;

        for (var i = 1; i < levels.Length; i++)
        {
            var diff = levels[i] - levels[i - 1];

            // Check if the difference is within the range [1, 3] or [-3, -1]
            if (Math.Abs(diff) < 1 || Math.Abs(diff) > 3)
            {
                return false;
            }

            // Determine the trend
            if (isIncreasing == null)
            {
                if (diff > 0)
                {
                    isIncreasing = true;
                }
                else if (diff < 0)
                {
                    isIncreasing = false;
                }
            }
            else
            {
                // Validate the trend
                if ((isIncreasing == true && diff < 0) || (isIncreasing == false && diff > 0))
                {
                    return false;
                }
            }
        }

        return true;
    }
    
    static bool CanBeSafeWithOneRemoval(int[] levels)
    {
        for (var i = 0; i < levels.Length; i++)
        {
            // Create a new array without the i-th level
            var modifiedLevels = levels.Where((_, index) => index != i).ToArray();

            if (IsSafe(modifiedLevels))
            {
                return true;
            }
        }

        return false;
    }
